            }
            if ((Flags & 0xFF000000) != 0xAA000000)
            {
                if (Bit(24)) bw.Write(Unk21);
                if (Bit(25)) bw.Write(PreDelayVariable);
                if (Bit(26)) bw.Write(StartOffsetVariable);
                if (Bit(27)) bw.Write(Unk22);
